mapper.sql:
from banner a
left join banner_company b on a.id=b.banner_id
left join linkable_button c on a.linkable_id=c.id


		and a.name like concat('%',#{name},'%')


		and c.link_type=#{linkType}


		and a.belong_page!=3	


		and a.belong_page=#{belongPage}


		and a.enabled=#{enabled}


		and a.platform_id=#{platformId}


		and a.company_type=#{companyType}




				and a.belong_page= 3 


				and (a.belong_page = 1 or a.belong_page = 2 or a.belong_page = 4)




		and b.company_id in

			#{id}



		and a.id in

			#{id}


	and a.belong_page > 0

mapper.select:
SELECT

FROM banner


		and img_url = #{po.imgUrl}


		and name = #{po.name}


		and sort = #{po.sort}


		and linkable_id = #{po.linkableId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and enabled = #{po.enabled}


		and remark = #{po.remark}


		and platform_id=#{po.platformId}


		and company_type=#{po.companyType}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM banner


		and img_url = #{po.imgUrl}


		and name = #{po.name}


		and sort = #{po.sort}


		and linkable_id = #{po.linkableId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and enabled = #{po.enabled}


		and remark = #{po.remark}


		and platform_id=#{po.platformId}


		and company_type=#{po.companyType}

SELECT

FROM banner


		and img_url = #{po.imgUrl}


		and name = #{po.name}


		and sort = #{po.sort}


		and linkable_id = #{po.linkableId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}d


		and enabled = #{po.enabled}


		and remark = #{po.remark}


		and platform_id=#{po.platformId}


		and company_type=#{po.companyType}




		ORDER BY ${page.orderBy}


		ORDER BY id desc

SELECT
count(1) cnt
FROM banner b
RIGHT JOIN 
(SELECT bc.banner_id FROM banner_company bc


			bc.company_id IN 

				#{id} 



GROUP BY bc.banner_id) bc ON bc.banner_id = b.id 
LEFT JOIN linkable_button lb on b.linkable_id = lb.id 


		AND b.name LIKE CONCAT('%','${po.name}','%')  


		AND lb.link_type = #{po.linkType}   


		and b.platform_id=#{po.platformId}


		and b.company_type=#{po.companyType}

SELECT b.id, b.name, b.img_url, lb.link_type FROM banner b RIGHT JOIN (SELECT bc.banner_id FROM banner_company bc
bc.company_id IN
#{id}
GROUP BY bc.banner_id) bc ON bc.banner_id = b.id LEFT JOIN linkable_button lb on b.linkable_id = lb.id
AND b.name LIKE CONCAT('%','${po.name}','%')
AND lb.link_type = #{po.linkType}
and b.platform_id=#{po.platformId}
and b.company_type=#{po.companyType}
limit #{page.limitStart},#{page.pageSize}
