mapper.select:
SELECT

FROM cms_cfg_key t


		and element_id = #{po.elementId}


		and name = #{po.name}


		and code = #{po.code}


		and t.type = #{po.type}


		and sort = #{po.sort}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and cfg_group = #{po.cfgGroup}


		and description = #{po.description}


		and cfg_kind = #{po.cfgKind}


		and parent_id = #{po.parentId}


		and label = #{po.label}


		and mandatory = #{po.mandatory}


		and mandatory_condition = #{po.mandatoryCondition}




		ORDER BY ${page.orderBy}


		ORDER BY sort		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM cms_cfg_key t


		and element_id = #{po.elementId}


