mapper.select:
SELECT

FROM cms_inst_cfg


		and cms_inst_id = #{po.cmsInstId}


		and cms_cfg_value_code = #{po.cmsCfgValueCode}


		and inst_text_value = #{po.instTextValue}


		and cms_cfg_key_id = #{po.cmsCfgKeyId}


		and banner_id = #{po.bannerId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and parent_id = #{po.parentId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM cms_inst_cfg


		and cms_inst_id = #{po.cmsInstId}


		and cms_cfg_value_code = #{po.cmsCfgValueCode}


		and inst_text_value = #{po.instTextValue}


		and cms_cfg_key_id = #{po.cmsCfgKeyId}


		and banner_id = #{po.bannerId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and parent_id = #{po.parentId}

SELECT

FROM cms_inst_cfg


		and cms_inst_id = #{po.cmsInstId}


		and cms_cfg_value_code = #{po.cmsCfgValueCode}


		and inst_text_value = #{po.instTextValue}


		and cms_cfg_key_id = #{po.cmsCfgKeyId}


		and banner_id = #{po.bannerId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and parent_id = #{po.parentId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc

SELECT ci.cms_element_id, ci.sort, cc.cms_inst_id, cc.cms_cfg_key_id, ck.code cfg_key_code, ck.type cfg_key_type, ck.description cfg_key_description, cc.cms_cfg_value_code, cc.inst_text_value, cc.banner_id, cc.id, cc.parent_id, ci.id inst_id, ci.status inst_status FROM cms_inst ci LEFT JOIN cms_element ce ON ci.cms_element_id=ce.id LEFT JOIN cms_inst_cfg cc ON ci.id=cc.cms_inst_id LEFT JOIN cms_cfg_key ck ON cc.cms_cfg_key_id=ck.id WHERE ci.cms_page_id=#{pageId}
AND ci.id IN
#{instId}
ORDER BY ci.sort,ck.sort,ck.id,cc.id
