mapper.select:
SELECT

FROM cms_inst


		and cms_page_id = #{po.cmsPageId}


		and cms_element_id = #{po.cmsElementId}


		and name = #{po.name}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and status = #{po.status}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM cms_inst


		and cms_page_id = #{po.cmsPageId}


		and cms_element_id = #{po.cmsElementId}


		and name = #{po.name}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and status = #{po.status}

SELECT i.id, i.cms_page_id, i.cms_element_id, i.name, i.create_time, i.update_time, i.sort, i.status FROM cms_inst i LEFT JOIN cms_inst_company cic ON i.id = cic.inst_id
and i.status = 1
and (cic.company_id = #{companyId} OR cic.company_id = #{companyAllId})
and cms_page_id = #{po.cmsPageId}
ORDER BY ${page.orderBy}
ORDER BY sort
limit #{page.limitStart},#{page.pageSize}

SELECT

FROM cms_inst


		and cms_page_id = #{po.cmsPageId}


		and cms_element_id = #{po.cmsElementId}


		and name = #{po.name}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and status = #{po.status}




		ORDER BY ${page.orderBy}


		ORDER BY id desc
