mapper.select:
SELECT

FROM cms_main_cfg t


		and id = #{po.id}


		and company_id = #{po.companyId}


		and temp_type = #{po.tempType}


		and t.banner_header = #{po.bannerHeader}


		and banner_middle = #{po.bannerMiddle}


		and self_product = #{po.selfProduct}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and recommendation = #{po.recommendation}


		and update_user = #{po.updateUser}




		ORDER BY ${page.orderBy}


		ORDER BY id		



limit #{page.limitStart},#{page.pageSize}

