mapper.select:
SELECT

FROM cms_page_cfg


		and cms_page_id = #{po.cmsPageId}


		and cms_cfg_value_code = #{po.cmsCfgValueCode}


		and text_value = #{po.textValue}


		and cms_cfg_key_id = #{po.cmsCfgKeyId}


		and banner_id = #{po.bannerId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and parent_id = #{po.parentId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM cms_page_cfg


		and cms_page_id = #{po.cmsPageId}


		and cms_cfg_value_code = #{po.cmsCfgValueCode}


		and text_value = #{po.textValue}


		and cms_cfg_key_id = #{po.cmsCfgKeyId}


		and banner_id = #{po.bannerId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and parent_id = #{po.parentId}

SELECT

FROM cms_page_cfg


		and cms_page_id = #{po.cmsPageId}


		and cms_cfg_value_code = #{po.cmsCfgValueCode}


		and text_value = #{po.textValue}


		and cms_cfg_key_id = #{po.cmsCfgKeyId}


		and banner_id = #{po.bannerId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and parent_id = #{po.parentId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc
