mapper.select:
SELECT

FROM cms_page_tab_company


		and page_tab_id = #{po.pageTabId}


		and status = #{po.status}


		and show_platform_logo = #{po.showPlatformLogo}


		and show_client_logo = #{po.showClientLogo}


		and client_type = #{po.clientType}


		and platform_id = #{po.platformId}


		and company_id = #{po.companyId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

