mapper.select:
SELECT

FROM cms_page_tab


		and name = #{po.name}


		and value = #{po.value}


		and default_value = #{po.defaultValue}


		and icon_url = #{po.iconUrl}


		and icon_default_url = #{po.iconDefaultUrl}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and type = #{po.type}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM cms_page_tab


		and name = #{po.name}


		and value = #{po.value}


		and default_value = #{po.defaultValue}


		and icon_url = #{po.iconUrl}


		and icon_default_url = #{po.iconDefaultUrl}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and type = #{po.type}

SELECT

FROM cms_page_tab


		and name = #{po.name}


		and value = #{po.value}


		and default_value = #{po.defaultValue}


		and icon_url = #{po.iconUrl}


		and icon_default_url = #{po.iconDefaultUrl}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and type = #{po.type}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id

SELECT
	cpt.id,
	cpt.name,
	cpt.value,
	cpt.default_value,
	cpt.icon_url,
	cpt.icon_default_url,
	cpt.icon_onclick_url,
	cpt.icon_onclick_default_url,
	cpt.page_tab_url,
	cptc.status,
	cptc.show_platform_logo,
	cptc.show_client_logo,
	cpt.create_time,
	cpt.update_time,
	cptc.client_type,
	cptc.platform_id,
	cpt.type,
	cpt.sort,
	cptc.company_id,
	cptc.id pageTabCompanyId
FROM cms_page_tab cpt
left join cms_page_tab_company cptc
ON cpt.id=cptc.page_tab_id


		and cpt.name = #{po.name}


		and cpt.value = #{po.value}


		and cpt.default_value = #{po.defaultValue}


		and cpt.icon_url = #{po.iconUrl}


		and cpt.icon_default_url = #{po.iconDefaultUrl}


		and cptc.status = #{po.status}


		and cptc.show_platform_logo = #{po.showPlatformLogo}


		and cptc.show_client_logo = #{po.showClientLogo}


		and cpt.create_time = #{po.createTime}


		and cpt.update_time = #{po.updateTime}


		and cptc.client_type = #{po.clientType}


		and cptc.platform_id = #{po.platformId}


		and cpt.type = #{po.type}


		and cptc.company_id = #{po.companyId}




		ORDER BY cpt.id

SELECT cpt.id, cpt.name, cpt.value, cpt.default_value, cpt.icon_url, cpt.icon_default_url, cpt.icon_onclick_url, cpt.icon_onclick_default_url, cpt.page_tab_url, cpt.create_time, cpt.update_time, cpt.type, cpt.sort FROM cms_page_tab cpt
cpt.type IN (1,3,4) AND cpt.platform_id=#{platformId}
ORDER BY cpt.id
