mapper.select:
SELECT

FROM cms_template


		and name = #{po.name}


		and status = #{po.status}


		and client_type = #{po.clientType}


		and client_version_a_id = #{po.clientVersionAId}


		and client_version_i_id = #{po.clientVersionIId}


		and client_version_a_remark = #{po.clientVersionARemark}


		and client_version_i_remark = #{po.clientVersionIRemark}


		and client_version_a_code = #{po.clientVersionACode}


		and client_version_i_code = #{po.clientVersionICode}


		and remark = #{po.remark}


		and type = #{po.type}


		and show_fgj = #{po.showFgj}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT

FROM cms_template


		and name  like concat('%',#{po.name},'%')


		and status = #{po.status}


		and client_type = #{po.clientType}


		and client_version_a_id = #{po.clientVersionAId}


		and client_version_i_id = #{po.clientVersionIId}


		and client_version_a_remark = #{po.clientVersionARemark}


		and client_version_i_remark = #{po.clientVersionIRemark}


		and client_version_a_code = #{po.clientVersionACode}


		and client_version_i_code = #{po.clientVersionICode}


		and remark = #{po.remark}


		and type = #{po.type}


		and show_fgj = #{po.showFgj}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}



			and type = 1	


			and type != 1	





		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

