mapper.sql:
FROM navigation_bar


		and navigation_bar_type = #{po.navigationBarType}


		and navigation_bar_name like concat('%',#{po.navigationBarName},'%')


		and navigation_bar_status = #{po.navigationBarStatus}


		and update_user = #{po.updateUser}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}


		and company_type = #{po.companyType}


		and id in 

			#{id}

