mapper.select:
SELECT

FROM navigation_label


		and navigation_bar_id = #{po.navigationBarId}


		and linkable_button_id = #{po.linkableButtonId}


		and navigation_label_name = #{po.navigationLabelName}


		and navigation_label_remark = #{po.navigationLabelRemark}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM navigation_label


		and navigation_bar_id = #{po.navigationBarId}


		and linkable_button_id = #{po.linkableButtonId}


		and navigation_label_name = #{po.navigationLabelName}


		and navigation_label_remark = #{po.navigationLabelRemark}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}

