mapper.select:
SELECT

FROM page_tab


		and page_tab_name = #{po.pageTabName}


		and page_tab_remark = #{po.pageTabRemark}


		and page_tab_sort = #{po.pageTabSort}


		and is_home_page = #{po.isHomePage}


		and page_tab_status = #{po.pageTabStatus}


		and web_template_id = #{po.webTemplateId}


		and company_id = #{po.companyId}


		and update_user = #{po.updateUser}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

