mapper.select:
SELECT

FROM su_list


		and title_name = #{po.titleName}


		and title_color = #{po.titleColor}


		and relation_type = #{po.relationType}


		and sort_type = #{po.sortType}


		and inst_id = #{po.instId}


		and max_show = #{po.maxShow}


		and show_type = #{po.showType}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and suc_id = #{po.sucId}


		and linkable_id = #{po.linkableId}


		and banner_url = #{po.bannerUrl}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

