mapper.delete:
delete from cms_element 


		and name = #{name}


		and code = #{code}


		and group_type = #{groupType}


		and client_version_a_id = #{clientVersionAId}


		and client_version_i_id = #{clientVersionIId}


		and client_version_a_remark = #{clientVersionARemark}


		and client_version_i_remark = #{clientVersionIRemark}


		and client_version_a_code = #{clientVersionACode}


		and client_version_i_code = #{clientVersionICode}


		and create_time = #{createTime}


		and update_time = #{updateTime}


		and sort = #{sort}
