mapper.update:
update template
set status=0
where id=#{id}

update template
set status=1
where client_type=#{clientType}
and platform_id=#{platformId}
and type=#{type}
and company_type=#{companyType}

mapper.delete:
delete from template


		and name = #{name}


		and status = #{status}


		and client_type = #{clientType}


		and client_version_a = #{clientVersionA}


		and client_version_i = #{clientVersionI}


		and remark = #{remark}


		and type = #{type}


		and create_time = #{createTime}


		and update_time = #{updateTime}


		and show_fgj=#{showFgj}
