mapper.select:
SELECT

FROM log_dict


		and parent_id = #{po.parentId}


		and name = #{po.name}


		and short_code = #{po.shortCode}


		and operatorType = #{po.operatortype}


		and arealevel = #{po.arealevel}


		and oper_code_id = #{po.operCodeId}


		and description = #{po.description}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM log_dict


		and parent_id = #{po.parentId}


