SELECT

FROM temp_recharge


		and rownum = #{po.rownum}


		and user_name = #{po.userName}


		and member_code = #{po.memberCode}


		and email = #{po.email}


		and user_id = #{po.userId}


		and sum = #{po.sum}


		and company_id = #{po.companyId}


		and company_name = #{po.companyName}


		and imp_res = #{po.impRes}


		and sn = #{po.sn}


		and generated_time = #{po.generatedTime}


		and tmpl_type = #{po.tmplType}


		and tmpl_name = #{po.tmplName}


		and account_type = #{po.accountType}


		and reason_id = #{po.reasonId}


		and batch_reason = #{po.batchReason}


		and flow_reason = #{po.flowReason}


		and remark = #{po.remark}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and status = #{po.status}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

