mapper.update:
update account_batch
set status=#{status}
where id=#{id}

mapper.delete:
delete from account_batch 


		and outflow_accountid=#{outflowAccountid}


		and inflow_accountid=#{inflowAccountid},


		and ra_batch = #{raBatch}


		and fin_batch = #{finBatch}


		and sum = #{sum}


		and reason_id = #{reasonId}


		and remark = #{remark}


		and flow_amount = #{flowAmount}


		and status = #{status}


		and operator_id = #{operatorId}


		and create_time = #{createTime}


		and update_time = #{updateTime}


		and type=#{type}


		and company_id=#{companyId}
