SELECT

FROM contract_item


		and contract_id = #{po.contractId}


		and order_code = #{po.orderCode}


		and user_id = #{po.userId}


		and merchant_product_id = #{po.merchantProductId}


		and merchant_product_name = #{po.merchantProductName}


		and merchant_product_standard = #{po.merchantProductStandard}


		and merchant_product_material = #{po.merchantProductMaterial}


		and merchant_product_place_of_origin = #{po.merchantProductPlaceOfOrigin}


		and merchant_product_item_amount = #{po.merchantProductItemAmount}


		and merchant_product_price_final = #{po.merchantProductPriceFinal}


		and merchant_product_remark = #{po.merchantProductRemark}


		and merchant_product_item_num = #{po.merchantProductItemNum}


		and merchant_id = #{po.merchantId}


		and contract_code = #{po.contractCode}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}


		and sku_id = #{po.skuId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

