mapper.select:
SELECT

FROM contract


		and order_code = #{po.orderCode}


		and contracting_place = #{po.contractingPlace}


		and sellers_type = #{po.sellersType}


		and user_id = #{po.userId}


		and purchaser_company_name = #{po.purchaserCompanyName}


		and purchaser_phone = #{po.purchaserPhone}


		and purchaser_fax = #{po.purchaserFax}


		and purchaser_address = #{po.purchaserAddress}


		and sellers_company_name = #{po.sellersCompanyName}


		and sellers_phone = #{po.sellersPhone}


		and sellers_fax = #{po.sellersFax}


		and sellers_address = #{po.sellersAddress}


		and delivery_date = #{po.deliveryDate}


		and delivery_address = #{po.deliveryAddress}


		and freight_and_delivery_charges = #{po.freightAndDeliveryCharges}


		and terms_of_payment_and_time_limit = #{po.termsOfPaymentAndTimeLimit}


		and product_amount = #{po.productAmount}


		and remarks = #{po.remarks}


		and platform_id = #{po.platformId}


		and merchant_id = #{po.merchantId}


		and contract_code = #{po.contractCode}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

