mapper.select:
SELECT

FROM delivery_company


		and name = #{po.name}


		and coding = #{po.coding}


		and trajectory_querying = #{po.trajectoryQuerying}


		and electronic_surface = #{po.electronicSurface}


		and pick_up = #{po.pickUp}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

