mapper.select:
SELECT

FROM distribution_mode_item


		and merchant_Id = #{po.merchantId}


		and shipping_code = #{po.shippingCode}


		and distribution_mode = #{po.distributionMode}


		and is_default = #{po.isDefault}


		and platform_id = #{po.platformId}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM distribution_mode_item


		and merchant_Id = #{po.merchantId}


		and shipping_code = #{po.shippingCode}


		and distribution_mode = #{po.distributionMode}


		and is_default = #{po.isDefault}


		and platform_id = #{po.platformId}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}

