mapper.select:
SELECT

FROM do_item


		and do_code = #{po.doCode}


		and ORDER_CODE = #{po.orderCode}


		and USER_ID = #{po.userId}


		and MERCHANT_ID = #{po.merchantId}


		and PRODUCT_ID = #{po.productId}


		and mp_id = #{po.mpId}


		and WAREHOUSE_ID = #{po.warehouseId}


		and PRODUCT_ITEM_AMOUNT = #{po.productItemAmount}


		and PRODUCT_PRICE_FINAL = #{po.productPriceFinal}


		and PRODUCT_ITEM_NUM = #{po.productItemNum}


		and PRODUCT_CNAME = #{po.productCname}


		and PRODUCT_ENAME = #{po.productEname}


		and PRODUCT_PIC_PATH = #{po.productPicPath}


		and PRODUCT_VERSION_NO = #{po.productVersionNo}


		and PRODUCT_PRICE_ORIGINAL = #{po.productPriceOriginal}


		and PRODUCT_PRICE_MARKET = #{po.productPriceMarket}


		and PRODUCT_PRICE_SALE = #{po.productPriceSale}


		and PRODUCT_TYPE = #{po.productType}


		and VIRTUAL_STOCK_STATUS = #{po.virtualStockStatus}


		and so_item_id = #{po.soItemId}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}


		and sku_id = #{po.skuId}




		ORDER BY ${page.orderBy}


		ORDER BY ID desc		



limit #{page.limitStart},#{page.pageSize}

