		and GOOD_RECEIVER_COUNTRY_ID = #{po.goodReceiverCountryId}


		and GOOD_RECEIVER_COUNTRY = #{po.goodReceiverCountry}


		and GOOD_RECEIVER_PROVINCE_ID = #{po.goodReceiverProvinceId}


