		and GOOD_RECEIVER_CITY_ID = #{po.goodReceiverCityId}


		and GOOD_RECEIVER_CITY = #{po.goodReceiverCity}


		and GOOD_RECEIVER_COUNTY_ID = #{po.goodReceiverCountyId}


