		and GOOD_RECEIVER_AREA_ID = #{po.goodReceiverAreaId}


		and GOOD_RECEIVER_AREA = #{po.goodReceiverArea}


		and ORDER_INVOICE_TYPE = #{po.orderInvoiceType}


		and status = #{po.status}


