mapper.select:
SELECT

FROM express_map_config


		and delivery_type = #{po.deliveryType}


		and delivery_company_name = #{po.deliveryCompanyName}


		and express_company_id = #{po.expressCompanyId}


		and express_company_name = #{po.expressCompanyName}


		and delivery_company_id = #{po.deliveryCompanyId}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
