mapper.select:
SELECT

FROM frozen_pu


		and sku_id = #{po.skuId}


		and pu_id = #{po.puId}


		and so_id = #{po.soId}


		and so_child_id = #{po.soChildId}


		and frozen_count = #{po.frozenCount}


		and freeze_status = #{po.freezeStatus}


		and unfreeze_reason = #{po.unfreezeReason}


		and unfreeze_time = #{po.unfreezeTime}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM frozen_pu


		and sku_id = #{po.skuId}


		and pu_id = #{po.puId}


		and so_id = #{po.soId}


		and so_child_id = #{po.soChildId}


		and frozen_count = #{po.frozenCount}


		and freeze_status = #{po.freezeStatus}


		and unfreeze_reason = #{po.unfreezeReason}


		and unfreeze_time = #{po.unfreezeTime}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}

SELECT

FROM frozen_pu


		and sku_id = #{po.skuId}


		and pu_id = #{po.puId}


		and so_id = #{po.soId}


		and so_child_id = #{po.soChildId}


		and frozen_count = #{po.frozenCount}


		and freeze_status = #{po.freezeStatus}


		and unfreeze_reason = #{po.unfreezeReason}


		and unfreeze_time = #{po.unfreezeTime}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


