mapper.select:
SELECT

FROM invoice


		and title_content = #{po.titleContent}


		and taxpayer_identification_code = #{po.taxpayerIdentificationCode}


		and register_addr = #{po.registerAddr}


		and register_tel = #{po.registerTel}


		and deposit_bank = #{po.depositBank}


		and bank_account = #{po.bankAccount}


		and business_licence_url = #{po.businessLicenceUrl}


		and user_id = #{po.userId}


		and is_default = #{po.isDefault}


		and create_type = #{po.createType}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}


		and title_type = #{po.titleType}


		and is_delete = #{po.isDelete}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
