SELECT

FROM limit_rule


		and serial_number = #{po.serialNumber}


		and type = #{po.type}


		and name = #{po.name}


		and is_start = #{po.isStart}


		and standard_unit_id = #{po.standardUnitId}


		and standard_unit_serial_number = #{po.standardUnitSerialNumber}


		and is_limit = #{po.isLimit}


		and su_limit_num = #{po.suLimitNum}


		and limit_time_type = #{po.limitTimeType}


		and limit_origin_time = #{po.limitOriginTime}


		and limit_stop_time = #{po.limitStopTime}


		and period_type = #{po.periodType}


		and user_limit_num = #{po.userLimitNum}


		and user_money_sum = #{po.userMoneySum}


		and create_userid = #{po.createUserid}


		and create_username = #{po.createUsername}


		and create_userip = #{po.createUserip}


		and create_usermac = #{po.createUsermac}


		and create_time = #{po.createTime}


		and update_userid = #{po.updateUserid}


		and update_username = #{po.updateUsername}


		and update_userip = #{po.updateUserip}


		and update_usermac = #{po.updateUsermac}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and limit_target = #{po.limitTarget}


		and su_comb_id = #{po.suCombId}


		and su_limit_amount = #{po.suLimitAmount}


		and company_type = #{po.companyType}


		and limit_unit = #{po.limitUnit}


		and is_user_limit = #{po.isUserLimit}


		and is_company_limit = #{po.isCompanyLimit}


		and company_limit_num = #{po.companyLimitNum}


		and company_money_sum = #{po.companyMoneySum}


		and is_store_limit = #{po.isStoreLimit}


		and store_limit_num = #{po.storeLimitNum}


		and store_money_sum = #{po.storeMoneySum}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

