		and company_money_sum = #{po.companyMoneySum}


		and is_store_limit = #{po.isStoreLimit}


		and store_limit_num = #{po.storeLimitNum}


		and store_money_sum = #{po.storeMoneySum}




		ORDER BY ${page.orderBy}


		ORDER BY id desc

SELECT lr.id, lr.serial_number, lr.type, lr.name, lr.is_start, lr.standard_unit_id, lr.standard_unit_serial_number, lr.is_limit, lr.su_limit_num, lr.limit_time_type, lr.limit_origin_time, lr.limit_stop_time, lr.period_type, lr.user_limit_num, lr.user_money_sum, lr.platform_id, lr.limit_target, lr.su_comb_id, lr.su_limit_amount, lr.company_type, lr.limit_unit, lr.is_user_limit, lr.is_company_limit, lr.company_limit_num, lr.company_money_sum, lr.is_store_limit, lr.store_limit_num, lr.store_money_sum FROM limit_rule lr
lr.is_start = 1
and (
lr.standard_unit_id = #{standardUnitId}
or
lr.su_comb_id in
#{suCombId}
)
and lr.platform_id = #{platformId}
group by lr.id ORDER BY lr.create_time
