mapper.select:
SELECT

FROM platfrom_condition


		and menu_code = #{po.menuCode}


		and condition_pool_id = #{po.conditionPoolId}


		and menu_name = #{po.menuName}


		and page_name = #{po.pageName}


		and condition_value = #{po.conditionValue}


		and condition_name = #{po.conditionName}


		and active = #{po.active}


		and is_dft = #{po.isDft}


		and condition_type = #{po.conditionType}


		and layout_sort = #{po.layoutSort}


		and platform_id = #{po.platformId}


		and data_type = #{po.dataType}


		and group_name = #{po.groupName}


		and tbody_value = #{po.tbodyValue}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM platfrom_condition


