mapper.select:
SELECT

FROM refundment


		and order_code = #{po.orderCode}


		and return_code = #{po.returnCode}


		and refundment_code = #{po.refundmentCode}


		and amount = #{po.amount}


		and channel = #{po.channel}


		and apply_time = #{po.applyTime}


		and refundment_time = #{po.refundmentTime}


		and voucher = #{po.voucher}


		and refundment_status = #{po.refundmentStatus}


		and refundment_type = #{po.refundmentType}


		and platform_id = #{po.platformId}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

