SELECT

FROM so_child_flow


		and so_child_id = #{po.soChildId}


		and operate = #{po.operate}


		and operator_id = #{po.operatorId}


		and do_mail = #{po.doMail}


		and creat_time = #{po.creatTime}


		and update_time = #{po.updateTime}


		and so_id = #{po.soId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

