mapper.select:
SELECT

FROM so_coupon_item


		and so_coupon_id = #{po.soCouponId}


		and order_code = #{po.orderCode}


		and parent_order_code = #{po.parentOrderCode}


		and coupon_code = #{po.couponCode}


		and mp_id = #{po.mpId}


		and product_item_num = #{po.productItemNum}


		and coupon_amount = #{po.couponAmount}


		and status = #{po.status}


		and coupon_id = #{po.couponId}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}


		and sku_id = #{po.skuId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM so_coupon_item


		and so_coupon_id = #{po.soCouponId}


