mapper.select:
SELECT

FROM so_delivery_item


		and order_code = #{po.orderCode}


		and package_no = #{po.packageNo}


		and delivery_express_nbr = #{po.deliveryExpressNbr}


		and remark = #{po.remark}


		and delivery_date = #{po.deliveryDate}


		and estimate_receive_date = #{po.estimateReceiveDate}


		and delivery_remark = #{po.deliveryRemark}


		and deliverier_name = #{po.deliverierName}


		and deliverier_mobile = #{po.deliverierMobile}


		and platform_id = #{po.platformId}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

