SELECT

FROM so_delivery


		and user_id = #{po.userId}


		and merchant_id = #{po.merchantId}


		and type = #{po.type}


		and so_id = #{po.soId}


		and so_child_id = #{po.soChildId}


		and order_code = #{po.orderCode}


		and delivery_company_id = #{po.deliveryCompanyId}


		and delivery_express_nbr = #{po.deliveryExpressNbr}


		and actual_storage_time = #{po.actualStorageTime}


		and actual_receipt_time = #{po.actualReceiptTime}


		and stock_out_no = #{po.stockOutNo}


		and remark = #{po.remark}


		and delivery_type = #{po.deliveryType}


		and delivery_remark = #{po.deliveryRemark}


		and package_code = #{po.packageCode}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

