mapper.select:
SELECT

FROM so_error_log


		and order_code = #{po.orderCode}


		and type = #{po.type}


		and oper_id = #{po.operId}


		and oper_name = #{po.operName}


		and description = #{po.description}


		and exception = #{po.exception}


		and error_code = #{po.errorCode}


		and error_message = #{po.errorMessage}


		and input_parameter = #{po.inputParameter}


		and output_parameter = #{po.outputParameter}


		and platform_id = #{po.platformId}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

