mapper.select:
SELECT

FROM so_installment


		and installment_code = #{po.installmentCode}


		and batch_num = #{po.batchNum}


		and order_code = #{po.orderCode}


		and pay_precondition = #{po.payPrecondition}


		and pay_status = #{po.payStatus}


		and pay_price = #{po.payPrice}


		and c_id = #{po.cId}


		and c_name = #{po.cName}


		and pay_time = #{po.payTime}


		and pay_comment = #{po.payComment}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

