SELECT

FROM so_invoice


		and user_id = #{po.userId}


		and so_id = #{po.soId}


		and so_child_id = #{po.soChildId}


		and invoice_value = #{po.invoiceValue}


		and invoice_code = #{po.invoiceCode}


		and invoice_tax_type = #{po.invoiceTaxType}


		and invoice_fin_type = #{po.invoiceFinType}


		and invoice_form = #{po.invoiceForm}


		and invoice_status = #{po.invoiceStatus}


		and invoice_title_type = #{po.invoiceTitleType}


		and invoice_title_content = #{po.invoiceTitleContent}


		and is_need_details = #{po.isNeedDetails}


		and invoice_content_type = #{po.invoiceContentType}


		and invoice_content = #{po.invoiceContent}


		and invoice_remark = #{po.invoiceRemark}


		and taxpayer_identification_code = #{po.taxpayerIdentificationCode}


		and receiver_mail = #{po.receiverMail}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

