mapper.select:
SELECT

FROM so_modify_price_item


		and order_code = #{po.orderCode}


		and parent_order_code = #{po.parentOrderCode}


		and mp_id = #{po.mpId}


		and product_item_amount = #{po.productItemAmount}


		and product_item_amount_after = #{po.productItemAmountAfter}


		and product_item_discount_amount = #{po.productItemDiscountAmount}


		and product_price_final = #{po.productPriceFinal}


		and product_price_discount = #{po.productPriceDiscount}


		and product_item_num = #{po.productItemNum}


		and product_cname = #{po.productCname}


		and product_ename = #{po.productEname}


		and code = #{po.code}


		and unit = #{po.unit}


		and place_of_origin = #{po.placeOfOrigin}


		and product_sale_type = #{po.productSaleType}


		and modify_id = #{po.modifyId}


		and so_item_id = #{po.soItemId}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}


		and sku_id = #{po.skuId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

