SELECT

FROM so_package_item


		and so_package_id = #{po.soPackageId}


		and order_code = #{po.orderCode}


		and parent_order_code = #{po.parentOrderCode}


		and user_id = #{po.userId}


		and merchant_id = #{po.merchantId}


		and product_id = #{po.productId}


		and mp_id = #{po.mpId}


		and warehouse_id = #{po.warehouseId}


		and product_item_amount = #{po.productItemAmount}


		and product_price_final = #{po.productPriceFinal}


		and product_item_num = #{po.productItemNum}


		and product_item_out_num = #{po.productItemOutNum}


		and product_cname = #{po.productCname}


		and product_ename = #{po.productEname}


		and product_pic_path = #{po.productPicPath}


		and product_version_no = #{po.productVersionNo}


		and product_sale_type = #{po.productSaleType}


		and product_price_original = #{po.productPriceOriginal}


		and product_price_market = #{po.productPriceMarket}


		and product_price_sale = #{po.productPriceSale}


		and product_tax_amount = #{po.productTaxAmount}


		and buy_type = #{po.buyType}


		and product_type = #{po.productType}


		and amount_share_coupon = #{po.amountShareCoupon}


		and amount_share_promotion = #{po.amountSharePromotion}


		and amount_share_delivery_fee = #{po.amountShareDeliveryFee}


		and amount_share_delivery_fee_accounting = #{po.amountShareDeliveryFeeAccounting}


		and product_gross_weight = #{po.productGrossWeight}


		and vehicle_warranty = #{po.vehicleWarranty}


		and delivery_time = #{po.deliveryTime}


		and delivery_method = #{po.deliveryMethod}


		and violation_responsibility = #{po.violationResponsibility}


		and source_id = #{po.sourceId}


		and code = #{po.code}


		and unit = #{po.unit}


		and place_of_origin = #{po.placeOfOrigin}


		and excise_tax_amount = #{po.exciseTaxAmount}


		and increment_tax_amount = #{po.incrementTaxAmount}


		and customs_duties_amount = #{po.customsDutiesAmount}


		and ext_info = #{po.extInfo}


		and so_item_id = #{po.soItemId}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}


		and sku_id = #{po.skuId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

