SELECT

FROM so_package


		and user_id = #{po.userId}


		and so_id = #{po.soId}


		and so_child_id = #{po.soChildId}


		and receiver_address_id = #{po.receiverAddressId}


		and order_code = #{po.orderCode}


		and merchant_id = #{po.merchantId}


		and so_child_delivery_fee = #{po.soChildDeliveryFee}


		and package_type = #{po.packageType}


		and delivery_code = #{po.deliveryCode}


		and delivery_status = #{po.deliveryStatus}


		and delivery_mode = #{po.deliveryMode}


		and delivery_company_id = #{po.deliveryCompanyId}


		and delivery_company_name = #{po.deliveryCompanyName}


		and delivery_message = #{po.deliveryMessage}


		and order_delivery_method_id = #{po.orderDeliveryMethodId}


		and good_receiver_name = #{po.goodReceiverName}


		and good_receiver_mobile = #{po.goodReceiverMobile}


		and pro_city_area = #{po.proCityArea}


		and delivery_date = #{po.deliveryDate}


		and good_receiver_address = #{po.goodReceiverAddress}


		and delivery_name = #{po.deliveryName}


		and delivery_name_mobile = #{po.deliveryNameMobile}


		and sign_name = #{po.signName}


		and sign_date = #{po.signDate}


		and sign_remark = #{po.signRemark}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc



limit #{page.limitStart},#{page.pageSize}

