SELECT

FROM so_package

	delivery_status <  3 and delivery_code is not null  and create_time < #{po.createTime}


		and user_id = #{po.userId}


		and so_id = #{po.soId}


		and so_child_id = #{po.soChildId}


		and receiver_address_id = #{po.receiverAddressId}


		and order_code = #{po.orderCode}


		and merchant_id = #{po.merchantId}


		and so_child_delivery_fee = #{po.soChildDeliveryFee}


		and package_type = #{po.packageType}


		and delivery_mode = #{po.deliveryMode}


		and delivery_company_id = #{po.deliveryCompanyId}


		and delivery_company_name = #{po.deliveryCompanyName}


