SELECT sp.id, sp.so_child_id, sp.so_child_delivery_fee, sp.delivery_code, sp.delivery_status, sp.delivery_company_name, sp.update_time, spb.so_box_code FROM so_package sp LEFT JOIN so_package_box spb ON sp.so_child_id = spb.so_child_id
and platform_id = #{po.platformId}
ORDER BY ${page.orderBy}
ORDER BY sp.so_child_id
limit #{page.limitStart},#{page.pageSize}
