mapper.select:
SELECT

FROM so_reminder


		and order_code = #{po.orderCode}


		and user_id = #{po.userId}


		and reminder_desc = #{po.reminderDesc}


		and platform_id = #{po.platformId}


		and merchant_id = #{po.merchantId}


		and user_phone = #{po.userPhone}


		and phone_record = #{po.phoneRecord}


		and reminder_type = #{po.reminderType}


		and ask_category_id = #{po.askCategoryId}


		and ask_category_desc = #{po.askCategoryDesc}


		and reminder_object = #{po.reminderObject}


		and ask_category_two_id = #{po.askCategoryTwoId}


		and status = #{po.status}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

