SELECT

FROM so_return_item


		and return_id = #{po.returnId}


		and order_code = #{po.orderCode}


		and merchant_id = #{po.merchantId}


		and mp_id = #{po.mpId}


		and product_price_final = #{po.productPriceFinal}


		and product_item_num = #{po.productItemNum}


		and piecework_unit = #{po.pieceworkUnit}


		and product_total_amount = #{po.productTotalAmount}


		and return_product_item_num = #{po.returnProductItemNum}


		and discount_amount = #{po.discountAmount}


		and coupon_amount = #{po.couponAmount}


		and purchase_amount = #{po.purchaseAmount}


		and product_cname = #{po.productCname}


		and product_pic_path = #{po.productPicPath}


		and so_item_id = #{po.soItemId}


		and product_price_sale = #{po.productPriceSale}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}


		and sku_id = #{po.skuId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

