mapper.select:
SELECT

FROM so_sign_config


		and order_status = #{po.orderStatus}


		and order_payment_status = #{po.orderPaymentStatus}


		and order_payment_type = #{po.orderPaymentType}


		and time_out = #{po.timeOut}


		and platform_id = #{po.platformId}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM so_sign_config


		and order_status = #{po.orderStatus}


		and order_payment_status = #{po.orderPaymentStatus}


		and order_payment_type = #{po.orderPaymentType}


		and time_out = #{po.timeOut}


		and platform_id = #{po.platformId}


		and update_time = #{po.updateTime}


		and create_time = #{po.createTime}

