count(1) cnt
FROM so_status_compensation


		and order_code = #{po.orderCode}


		and order_status = #{po.orderStatus}


		and order_business_type = #{po.orderBusinessType}


		and order_business_sub_type = #{po.orderBusinessSubType}


		and order_business_status = #{po.orderBusinessStatus}


