mapper.select:
SELECT

FROM so_thirdparty


		and so_child_id = #{po.soChildId}


		and thirdparty_id = #{po.thirdpartyId}


		and thirdparty_status = #{po.thirdpartyStatus}


		and user_name = #{po.userName}


		and id_card_num = #{po.idCardNum}


		and phone = #{po.phone}


		and oid_card_num = #{po.oidCardNum}


		and comment = #{po.comment}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM so_thirdparty


		and so_child_id = #{po.soChildId}


